attrs.script:
# Set address and headers
url     = "https://@@{address}@@:8443/era/v0.8/auth/update"
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Change Password
payload = {"password":"@@{EraAdmin.secret}@@"}
resp = urlreq(url, verb='POST', auth='BASIC', user='@@{EraAdmin.username}@@', passwd='Nutanix/4u', params=json.dumps(payload), headers=headers)
#resp = urlreq(url, verb='POST', params=json.dumps(payload), headers=headers)
if resp.ok:
  print "Password successfully changed."
else:
  print "Password change failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)

# Set address and headers
url     = "https://@@{address}@@:8443/era/v0.8/auth/validate"
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Change Password
payload = {"eulaAccepted": True}
resp = urlreq(url, verb='POST', auth='BASIC', user='@@{EraAdmin.username}@@', passwd='@@{EraAdmin.secret}@@', params=json.dumps(payload), headers=headers)
#resp = urlreq(url, verb='POST', params=json.dumps(payload), headers=headers)
if resp.ok:
  print "Eula successfully accepted."
else:
  print "Eula acception failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)

#!/bin/bash
set -ex

# Generate the Cluster Registration File
cat << EOF > ~/cluster_file.txt
{
  "name": "@@{CLSTR_NAME}@@_Cluster", 
  "description": "@@{CLSTR_NAME}@@ Cluster", 
  "cluster_details": {
    "default_storage_container": "@@{CTR_NAME}@@", 
    "protocol": "https", 
    "ip_address": "@@{PE_VIP}@@", 
    "port": "9440", 
    "creds_bag": {
      "username": "@@{pe_creds.username}@@", 
      "password": "@@{pe_creds.secret}@@"
    }
  }
}
EOF

# Register the cluster
era -c "cluster add input_file=~/cluster_file.txt"

# Remove the file to hide the creds
rm ~/cluster_file.txt
